import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Helper';
  welcomebuttonClicked = false;

  handleButtonClick(): void {
    alert('Button Clicked!');
    this.welcomebuttonClicked = (!this.welcomebuttonClicked)?true:false;
  }
}
<div class="full-display-image">
    <button *ngIf="!welcomebuttonClicked" (click)="handleButtonClick()" class="btn">WelCome</button>
    <div *ngIf="welcomebuttonClicked" class="container">
        <app-vendor></app-vendor>
    </div>
</div>
