import { Component } from '@angular/core';
import { ActivatedRoute, Router, Routes } from '@angular/router';

@Component({
  selector: 'app-contract',
  templateUrl: './contract.component.html',
  styleUrls: ['./contract.component.css']
})
export class ContractComponent {

  constructor(
    private router: Router,
    private activeroute: ActivatedRoute,
  ) {}
  
  OnViewContract() {
    console.log("navigate to view contract!");
    this.router.navigate(['contract'], {relativeTo: this.activeroute});
  }
}
<div class="container-xl">
	<div class="row justify-content-evenly" >
		<div class="col-sm-4">
			<button class="btn btn-primary" [routerLink]="['create-contract']" type="button">Create Contract</button>
		</div>
		<div class="col-sm-4">
			<button class="btn btn-primary" (click)="OnViewContract()" type="button">View Contracts</button>
        </div>
        
	</div>
    
</div>

