import { Component } from '@angular/core';

@Component({
  selector: 'app-create-contract',
  templateUrl: './create-contract.component.html',
  styleUrls: ['./create-contract.component.css']
})
export class CreateContractComponent {
  clickedSubmit = false;

}
<button class="btn btn-primary" type="button" routerLink="../">Back</button>
<div class="container" *ngIf="!clickedSubmit">
	<div class="container-customer">
		
	</div>
	<img src="https://i.gifer.com/ZSj6.gif" alt="handShake GIF">
	<div class="container-vendor">
		
	</div>

	<button class="btn btn-primary" type="button" (click)="clickedSubmit=true">Submit</button>
</div>

<div class="container-xs" *ngIf="clickedSubmit">
	<p style="color: green; font-size: larger;">Contract has been created Succesully!</p>
	
	<!-- <button class="btn btn-primary">Cancel</button> -->
	
	<button class="btn btn-primary" [routerLink]="['/contract/view-contracts']">Ok</button>
	
</div>
