import { Component } from '@angular/core';

@Component({
  selector: 'app-vendor',
  templateUrl: './vendor.component.html',
  styleUrls: ['./vendor.component.css']
})
export class VendorComponent {
  createButtonClicked = false;
  getExistButtonClicked = false;
}
<div class="container-sm" style="background-color: aliceblue; padding: 50px 20px 50px 20px; border-radius: .35rem;" *ngIf="!(getExistButtonClicked || createButtonClicked)">
	<div class="grid" style="row-gap: 1;">
		<div class="g-col-6">
			<button class="btn btn-primary" style="width: 200px; height: 40px;"  (click)="createButtonClicked = true">Create Contract</button>
		</div>
		<div class="g-col-6">
			<button class="btn btn-primary" style="width: 200px; height: 40px;" (click)="getExistButtonClicked = true">View Contracts</button>
		</div>
	</div>
    
</div>

