import { Component } from '@angular/core';

@Component({
  selector: 'app-contract',
  templateUrl: './contract.component.html',
  styleUrls: ['./contract.component.css']
})
export class ContractComponent {
  createButton = false;

  CreateButtonSet() {
    this.createButton = true;
  }
}
<div class="container-xl"  *ngIf="!createButton">
	<div class="row justify-content-evenly" >
		<div class="col-sm-4">
			<button class="btn btn-primary" (click)="CreateButtonSet()" type="button">Create Contract</button>
		</div>
		<div class="col-sm-4">
			<button class="btn btn-primary" [routerLink]="['view-contracts']" type="button">View Contracts</button>
        </div>

	</div>

</div>

<div class="container" *ngIf="createButton">
	<form method="post">
		<label for="name">Name</label>
		<input type="text" id="name">
	</form>
</div>
