import { Component } from '@angular/core';

@Component({
  selector: 'app-contract',
  templateUrl: './contract.component.html',
  styleUrls: ['./contract.component.css']
})
export class ContractComponent {
  createButtonClicked = false;
  getExistButtonClicked = false;
}
<div class="container" *ngIf="!(getExistButtonClicked || createButtonClicked)">
	<div class="row justify-content-evenly">
		<div class="col-sm-4">
			<button class="btn btn-primary" (click)="createButtonClicked = true">Create Contract</button>
		</div>
		<div class="col-sm-4">
			<button class="btn btn-primary" (click)="getExistButtonClicked = true">View Contracts</button>
		</div>
	</div>

</div>

<div class="container-create" *ngIf="!getExistButtonClicked">

</div>
